-- Create users table
CREATE TABLE users (
    user_id BIGINT NOT NULL, -- Using BIGINT for potential large user base, Node.js will handle ID generation
    email VARCHAR(255) NOT NULL UNIQUE,
    password_hash VARCHAR(255) NOT NULL,
    created_at VARCHAR(255) NOT NULL, -- Storing as string as per primitive type constraint
    updated_at VARCHAR(255) NOT NULL  -- Storing as string as per primitive type constraint
);

-- Create content table
CREATE TABLE content (
    content_id BIGINT NOT NULL, -- Using BIGINT for potential large content base
    title VARCHAR(255) NOT NULL,
    synopsis TEXT NOT NULL,
    poster_url VARCHAR(255) NOT NULL,
    content_type VARCHAR(50) NOT NULL CHECK (content_type IN ('"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'Movie'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"', '"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'TV Show'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"'"')),
    runtime_minutes INT NULL,
    seasons_count INT NULL,
    release_year INT NOT NULL,
    content_rating VARCHAR(50) NULL,
    average_crit_score REAL NULL, -- Using REAL for floating point scores
    created_at VARCHAR(255) NOT NULL, -- Storing as string as per primitive type constraint
    updated_at VARCHAR(255) NOT NULL  -- Storing as string as per primitive type constraint
);

-- Create genres table
CREATE TABLE genres (
    genre_id BIGINT NOT NULL, -- Using BIGINT for potential large genre list
    genre_name VARCHAR(100) NOT NULL UNIQUE
);

-- Create content_genres junction table
CREATE TABLE content_genres (
    content_id BIGINT NOT NULL,
    genre_id BIGINT NOT NULL,
    PRIMARY KEY (content_id, genre_id),
    FOREIGN KEY (content_id) REFERENCES content(content_id) ON DELETE CASCADE,
    FOREIGN KEY (genre_id) REFERENCES genres(genre_id) ON DELETE CASCADE
);

-- Create moods table
CREATE TABLE moods (
    mood_id BIGINT NOT NULL, -- Using BIGINT for potential large mood list
    mood_name VARCHAR(100) NOT NULL UNIQUE
);

-- Create streaming_services table
CREATE TABLE streaming_services (
    service_id BIGINT NOT NULL, -- Using BIGINT for potential large service list
    service_name VARCHAR(100) NOT NULL UNIQUE,
    service_logo_url VARCHAR(255) NULL
);

-- Create content_streaming_services junction table
CREATE TABLE content_streaming_services (
    content_id BIGINT NOT NULL,
    service_id BIGINT NOT NULL,
    PRIMARY KEY (content_id, service_id),
    FOREIGN KEY (content_id) REFERENCES content(content_id) ON DELETE CASCADE,
    FOREIGN KEY (service_id) REFERENCES streaming_services(service_id) ON DELETE CASCADE
);

-- Create recommendation_logs table
CREATE TABLE recommendation_logs (
    log_id BIGINT NOT NULL, -- Using BIGINT for potential large log volume
    session_id VARCHAR(255) NOT NULL,
    user_input_mood_ids JSON NOT NULL,
    user_input_genre_ids JSON NOT NULL,
    user_input_service_ids JSON NOT NULL,
    user_input_content_type VARCHAR(50) NOT NULL,
    recommended_content_id BIGINT NULL,
    recommendation_timestamp VARCHAR(255) NOT NULL, -- Storing as string as per primitive type constraint
    feedback_type VARCHAR(100) NULL,
    FOREIGN KEY (recommended_content_id) REFERENCES content(content_id) ON DELETE SET NULL
);

-- Create user_saved_recommendations table
CREATE TABLE user_saved_recommendations (
    user_saved_id BIGINT NOT NULL, -- Using BIGINT for potential large saved lists
    user_id BIGINT NOT NULL,
    content_id BIGINT NOT NULL,
    saved_at VARCHAR(255) NOT NULL, -- Storing as string as per primitive type constraint
    source_criteria JSON NULL,
    PRIMARY KEY (user_id, content_id), -- Prevent duplicate saves by a user for the same content
    FOREIGN KEY (user_id) REFERENCES users(user_id) ON DELETE CASCADE,
    FOREIGN KEY (content_id) REFERENCES content(content_id) ON DELETE CASCADE
);

